class ShootingStars{constructor(t="shooting-stars",n=3e3){this.containerId=t,this.interval=n,this.init()}init(){this.createStyle(),this.createContainer(),this.startShooting()}createStyle(){const t=document.createElement("style");t.textContent=`\n        #${this.containerId} {\n          position: fixed;\n          top: 0;\n          left: 0;\n          width: 100vw;\n          height: 100vh;\n          pointer-events: none;\n          z-index: 10;\n          overflow: hidden;\n        }\n  \n        .shooting-star {\n          position: absolute;\n          width: 2px;\n          height: 100px;\n          background: linear-gradient(180deg, rgba(255,255,255,1) 0%, rgba(255,255,255,0) 100%);\n          opacity: 0.8;\n          transform: rotate(45deg);\n          animation: shoot 1s linear forwards;\n          filter: drop-shadow(0 0 6px white);\n        }\n  \n        @keyframes shoot {\n          0% {\n            transform: translate(0, 0) rotate(45deg);\n            opacity: 1;\n          }\n          100% {\n            transform: translate(-1000px, 1000px) rotate(45deg);\n            opacity: 0;\n          }\n        }\n      `,document.head.appendChild(t)}createContainer(){const t=document.createElement("div");t.id=this.containerId,document.body.appendChild(t)}startShooting(){setInterval((()=>this.createShootingStar()),this.interval)}createShootingStar(){const t=document.createElement("div");t.classList.add("shooting-star");const n=Math.random()*window.innerWidth*.8+.1*window.innerWidth,e=Math.random()*window.innerHeight*.4;t.style.left=`${n}px`,t.style.top=`${e}px`,document.getElementById(this.containerId).appendChild(t),setTimeout((()=>{t.remove()}),1e3)}}document.addEventListener("DOMContentLoaded",(()=>{new ShootingStars}));